mapper.update:
update
	board
set
	subject  = #{subject}
	,writer  = #{writer}
	,content = #{content}

		,img_name = #{img_name}


		,img_name = null

where
	b_no=#{b_no}

mapper.insert:
insert  into board(
	b_no
	,subject
	,writer
	,readcount
	,content
	,reg_date

	,img_name

) values (
	(select nvl(max(b_no),0)+1 from board)
	,#{subject}
	,#{writer}  
	,0
	,#{content}
	,sysdate

	,#{img_name}

)
